INSERT INTO `news` (`id_news`, `name`, `preview_text`, `detail_text`, `id_cat`, `picture`) VALUES
(2, 'Trọng tài - vết đen của kỳ World Cup sôi động ', 'World Cup 2014 chưa đi hết lượt đầu vòng bảng nhưng các trọng tài đẳng cấp FIFA đã có tới bốn trận bị chỉ trích dữ dội.', '<p>World Cup 2014 chưa đi hết lượt đầu v&ograve;ng bảng nhưng c&aacute;c trọng t&agrave;i đẳng cấp FIFA đ&atilde; c&oacute; tới bốn trận bị chỉ tr&iacute;ch dữ dội.World Cup 2014 chưa đi hết lượt đầu v&ograve;ng bảng nhưng c&aacute;c trọng t&agrave;i đẳng cấp FIFA đ&atilde; c&oacute; tới bốn trận bị chỉ tr&iacute;ch dữ dội.</p>\r\n', 31, 'vne-1435650647.jpg'),
(3, 'Nhóm phượt ''cứu người được khen thưởng''', 'Sáng 6/9, Ủy ban An toàn giao thông quốc gia cùng Trung ương Đoàn TNCS Hồ Chí Minh tặng bằng khen cho đoàn phượt Phong Vân vì tham gia cứu hàng chục nạn nhân vụ xe khách Sao Việt lao xuống vực tại dốc Tòng Sành (Bát Xát, Lào Cai).', '<p>Tại buổi trao tặng, Bộ trưởng Giao th&ocirc;ng vận tải Đinh La Thăng cảm ơn đo&agrave;n phượt kh&ocirc;ng ngại trời tối, vực s&acirc;u, l&agrave; những người đầu ti&ecirc;n đưa h&agrave;ng chục nạn nh&acirc;n l&ecirc;n khỏi vực. &quot;T&ocirc;i đ&aacute;nh gi&aacute; cao tấm l&ograve;ng v&agrave; sự dũng cảm của c&aacute;c bạn, kh&ocirc;ng quản hiểm nguy tham gia cứu hộ. Ch&iacute;nh v&igrave; vậy mới giảm được thương vong&quot;, Bộ trưởng Thăng n&oacute;i. Ngo&agrave;i bằng khen, nh&oacute;m được thưởng 12 triệu đồng v&agrave; 16 chiếc mũ bảo hiểm c&ugrave;ng lời dặn &quot;đi phượt th&igrave; an to&agrave;n phải l&agrave; số một&quot;. Số tiền thưởng 500.000 đồng mỗi c&aacute; nh&acirc;n trước đ&oacute; từ Th&agrave;nh đo&agrave;n H&agrave; Nội đ&atilde; được Phong V&acirc;n ủng hộ lại cho quỹ từ thiện của Th&agrave;nh đo&agrave;n.</p>\r\n', 2, ''),
(18, '2 nhà máy vàng đóng cửa chưa có phương án trả nợ thuế', '2 nhà máy vàng đóng cửa chưa có phương án trả nợ thuế2 nhà máy vàng đóng cửa chưa có phương án trả nợ thuế', '<p>2 nh&agrave; m&aacute;y v&agrave;ng đ&oacute;ng cửa chưa c&oacute; phương &aacute;n trả nợ thuế2 nh&agrave; m&aacute;y v&agrave;ng đ&oacute;ng cửa chưa c&oacute; phương &aacute;n trả nợ thuế2 nh&agrave; m&aacute;y v&agrave;ng đ&oacute;ng cửa chưa c&oacute; phương &aacute;n trả nợ thuế2 nh&agrave; m&aacute;y v&agrave;ng đ&oacute;ng cửa chưa c&oacute; phương &aacute;n trả nợ thuế2 nh&agrave; m&aacute;y v&agrave;ng đ&oacute;ng cửa chưa c&oacute; phương &aacute;n trả nợ thuế2 nh&agrave; m&aacute;y v&agrave;ng đ&oacute;ng cửa chưa c&oacute; phương &aacute;n trả nợ thuế</p>\r\n', 4, 'vne-1435650311.JPG'),
(32, 'U19 Việt Nam 0-0 U19 Australia (hiệp hai)', 'U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)', '<p>U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)U19 Việt Nam 0-0 U19 Australia (hiệp hai)</p>\r\n', 31, 'vne-1435650662.jpg'),
(35, 'Ước gì con tôi không phải đi du học', 'Tôi ra sân bay Tân Sơn Nhất tiễn con đi Mỹ du học vào năm ngoái mà lòng nặng trĩu nỗi buồn. Sân bay đông nghẹt. Nhìn những em bé chỉ 15, 16 tuổi như con tôi một mình đẩy hành lý vào làm thủ tục ở sân bay mà rớt nước mắt.', 'Chuyến bay kéo dài 24 giờ đồng hồ, bao gồm cả transit ở Nhật Bản. Gia đình tôi chỉ còn biết cầu trời khấn Phật để mong bình an. Và tôi cũng như biết bao gia đình phải chờ đợi một năm học thì con mới về nghỉ hè. Không có gì có thể tả hết nỗi khổ của những người làm cha mẹ xa con. Cũng không có gì có thể nói hết về sự gian nan vất vả khi cha mẹ lao động cực nhọc kiếm tiền học phí cho con đi du học. Bởi cho một đứa con đi học xa nhà cần cả một tinh thần thép và một khả năng tài chính đủ mạnh. Cũng như chính đứa bé đó muốn thành công cũng phải vượt qua những thách thức không dễ dàng ở nơi chúng chưa bao giờ biết đến, trong môi trường học tập và cạnh tranh quốc tế.\r\n\r\nNhưng vì sao gia đình tôi và biết bao gia đình khác đã lựa chọn con đường này? Có lẽ vì chúng tôi muốn thoát ra khỏi nỗi lo lắng và buồn bực đã nặng trĩu trong lòng nhiều năm qua.', 2, ''),
(36, 'Đầu tư cho giới trẻ', 'Chắc hẳn nhiều bạn còn nhớ năm 2003, Việt Nam đoạt chức vô địch Sea Games 22 với 158 huy chương vàng, bứt xa Á quân Thái Lan với 90 huy chương vàng.', 'Dường như mọi người đều nghĩ rằng, nếu chỉ trong một thời gian ngắn mà Việt Nam từ một vị trí khiêm tốn đã tiến lên nắm chức vô địch thể thao trong vùng thì chắc hẳn rồi đây chúng ta cũng có thể tạo được nhiều “phép lạ” khác. Điển hình nhất là có vài người bạn Singapore lúc ấy đã hỏi tôi rằng: “Liệu trong vòng 10 năm nữa Việt Nam có thể vượt qua Thái Lan được không?”.\r\n\r\nKhi so sánh mức độ phát triển kinh tế thì thước đo thường được dùng nhất là con số “Thu nhập bình quân đầu người” (lấy tổng sản lượng quốc nội GDP, chia cho dân số một nước). Theo thống kê của The Economist thì trong vòng 7 năm từ 1995 đến 2002, Việt Nam là nước đã tiến nhanh nhất khi từ vị trí 151 lên đến 128 (tăng 23 bậc), trong khi Thái Lan lại tụt từ 69 xuống 84 (giảm 15 bậc). Như vậy là trong vòng 7 năm từ 1995 đến 2002, khoảng cách giữa Việt Nam và Thái Lan đã thu ngắn 38 bậc và nếu tiếp tục cái đà rút ngắn 5 bậc mỗi năm này thì phải chăng chỉ cần 8 năm, nghĩa là đến 2010 thì kinh tế Việt Nam sẽ bắt kịp Thái Lan? Thực ra vấn đề không hề đơn giản như vậy vì nhiều lý do mà trước hết lúc bấy giờ Việt Nam và Trung Quốc chưa “nối mạng” với kinh tế thế giới nên đã không bị ảnh hưởng nặng bởi cuộc khủng hoảng tài chính 1997-1999 như các nước khác. \r\n\r\nThấm thoắt mười năm đã trôi qua từ SEA Games 22 và hiện tại thì kết quả cuộc chạy đua kinh tế ấy ra sao rồi? Theo thống kê của IMF năm 2013 thì VN được xếp hạng 134 và Thái Lan 92. Như thế có nghĩa là trong vòng 10 năm qua thì cả hai nước đều… đi thụt lùi: VN từ 128 xuống 134 (giảm 6 bậc) và Thái Lan từ 84 xuống 92 (giảm 8 bậc).\r\n\r\nTrong bối cảnh hội nhập hiện tại, khi nền kinh tế Việt Nam ngày càng gắn liền hơn với kinh tế toàn cầu, quả thật là chúng ta đã và sẽ không thể tránh được ảnh hưởng của những cú “sốc” kinh tế toàn cầu, như cuộc khủng hoảng 2008 vừa qua mà ảnh hưởng vẫn còn dai dẳng cho đến hôm nay.', 2, ''),
(37, 'Mỹ hoài nghi về lệnh ngừng bắn ở Ukraine', 'Tổng thống Mỹ Barack Obama hôm qua nghi ngờ về lệnh ngừng bắn mới ở Ukraine và hứa hẹn về một lệnh trừng phạt mới để tiếp tục gây áp lực đối với Nga. ', '<p>&quot;Ch&uacute;ng t&ocirc;i hy vọng, nhưng dựa tr&ecirc;n những kinh nghiệm trong qu&aacute; khứ, ch&uacute;ng t&ocirc;i cũng ho&agrave;i nghi về việc liệu phe ly khai c&oacute; thực sự tu&acirc;n thủ, v&agrave; liệu Nga sẽ chấm dứt x&acirc;m phạm chủ quyền v&agrave; to&agrave;n vẹn l&atilde;nh thổ Ukraine hay kh&ocirc;ng&quot;, USA Today dẫn lời &ocirc;ng Obama h&ocirc;m qua n&oacute;i tại Wales, Anh. &quot;Điều đ&oacute; cần được kiểm tra&quot;. Để duy tr&igrave; &aacute;p lực của phương T&acirc;y, &ocirc;ng Obama cho biết Mỹ v&agrave; ch&acirc;u &Acirc;u đang ho&agrave;n tất c&aacute;c biện ph&aacute;p &quot;nhằm mở rộng v&agrave; tăng cường trừng phạt đối với c&aacute;c ng&agrave;nh ng&acirc;n h&agrave;ng, năng lượng v&agrave; quốc ph&ograve;ng Nga&quot;. Những lệnh trừng phạt mới n&agrave;y c&oacute; thể được dỡ bỏ nếu lệnh ngừng bắn v&agrave; một thỏa thuận h&ograve;a b&igrave;nh thực sự c&oacute; t&aacute;c dụng chấm dứt chiến sự giữa qu&acirc;n đội Ukraine v&agrave; phe ly khai th&acirc;n Nga. Tổng thống Mỹ cũng khen ngợi sự cứng rắn của li&ecirc;n minh phương T&acirc;y khi đối đầu với Nga, trong đ&oacute; c&oacute; việc th&agrave;nh lập lực lượng phản ứng nhanh để bảo vệ c&aacute;c nước th&agrave;nh vi&ecirc;n NATO ở Đ&ocirc;ng &Acirc;u. &Ocirc;ng cho rằng n&oacute; thể hiện nguy&ecirc;n tắc &quot;c&aacute;c nước lớn kh&ocirc;ng thể dậm ch&acirc;n l&ecirc;n c&aacute;c nước nhỏ&quot;. Trong khi đ&oacute;, Nga h&ocirc;m qua cũng hoan ngh&ecirc;nh việc k&yacute; kết thỏa thuận ngừng bắn. &quot;Moscow hy vọng tất cả c&aacute;c điều kiện trong văn bản v&agrave; c&aacute;c thỏa thuận đạt được sẽ được c&aacute;c b&ecirc;n tu&acirc;n thủ triệt để, v&agrave; qu&aacute; tr&igrave;nh đ&agrave;m ph&aacute;n sẽ tiếp tục cho tới khi cuộc khủng hoảng ở Ukraine được giải quyết đầy đủ&quot;, &ocirc;ng Dmitry Peskov, ph&aacute;t ng&ocirc;n vi&ecirc;n của Tổng thống Nga Vladimir Putin, n&oacute;i. &Ocirc;ng Peskov cũng nhấn mạnh thỏa thuận l&agrave; kết quả của s&aacute;ng kiến do &ocirc;ng Putin v&agrave; người đồng cấp Ukraine Petro Poroshenko đề xuất. Moscow b&aacute;c bỏ việc tham chiến ở Ukraine, nhưng Kiev v&agrave; NATO cho rằng qu&acirc;n thường trực cũng như xe tăng Nga đang chiến đấu c&ugrave;ng lực lượng ly khai ở đ&ocirc;ng Ukraine. Thỏa thuận ngừng bắn c&oacute; điều kiện h&ocirc;m qua được k&yacute; kết tại Minsk, Belarus v&agrave; c&oacute; hiệu lực từ 18h. Sự kiện c&oacute; sự tham gia của Ukraine, Nga, phe ly khai th&acirc;n Nga v&agrave; Tổ chức Hợp t&aacute;c v&agrave; An ninh ch&acirc;u &Acirc;u (OSCE).</p>\r\n', 3, 'vne-1435650097.jpeg'),
(39, 'Mỹ hối thúc NATO tiêu diệt Nhà nước Hồi giáo', 'Hôm qua, Mỹ tuyên bố tiêu diệt ba thành viên cấp cao của Nhà nước Hồi giáo trong một cuộc không kích của Mỹ ở Iraq.', 'Theo AFP, ông Obama tin rằng một liên minh chống lại Nhà nước Hồi giáo tự xưng (IS) đã được thành lập, sau cuộc họp thượng đỉnh Tổ chức Hiệp ước Bắc Đại Tây Dương (NATO) hôm qua. \r\n\r\n"Tôi không thấy sự phản đối hay thoái lui nào về việc chúng ta có vai trò trọng yếu trong việc cản bước tổ chức tàn bạo này, chúng đang gây hỗn loạn trong khu vực và gây hại đến quá nhiều người. Chúng cũng gây nên mối đe dọa dài hạn tới an toàn và an ninh của các thành viên NATO. Vì thế chúng ta phải hành động để làm suy yếu và cuối cùng tiêu diệt được Nhà nước Hồi giáo tự xưng", AP dẫn lời ông Obama nói trong phiên kết thúc hội nghị.\r\n\r\nTrong họp báo sau đó, ông Obama cho hay các thành viên NATO đang sẵn sàng cùng tham dự chiến dịch quân sự của Mỹ ở Iraq để truy đuổi phiến quân Hồi giáo. Trong vài tuần gần đây, Mỹ thực hiện hơn 100 cuộc không kích ở phía bắc Iraq, giúp các lực lượng chính phủ Iraq và Kurd giành lại các khu vực bị phiến quân chiếm giữ. Mỹ cũng bày tỏ hy vọng một chính phủ mới của Iraq sẽ được thiết lập trong tuần tới.\r\n\r\nTuy nhiên các đối tác châu Âu tỏ vẻ thận trọng. Anh, nước được cho là có đông công dân đứng trong hàng ngũ IS, để ngỏ khả năng không kích ở Iraq. Tuy nhiên Thủ tướng Anh David Cameron không nêu cao việc có hành động ngay lập tức. "Điều này cần thời gian, kiên nhẫn và quyết tâm", ông nói. \r\n\r\nTổng thống Pháp Francois Hollande cho hay nước này sẵn sàng tham gia liên minh với Mỹ chống lại phiến quân ở Iraq, với điều kiện sự tham gia của Pháp không có lợi cho chính quyền của Tổng thống Bashar al- Assad.\r\n\r\nNgoại trưởng Đức Frank-Walter Steinmeier nhấn mạnh nỗ lực chống phiến quân Hồi giáo đang ở giai đoạn đầu, chưa có chiến lược dài hạn.\r\n\r\nTổng thư ký NATO Anders Fogh Rasmussen cho biết liên minh quân sự sẵn sàng trợ giúp nếu có yêu cầu từ Baghdad, như là hỗ trợ huấn luyện và hợp tác với các nước chống lại IS. Các nước NATO cũng nhất trí trao đổi thêm thông tin về các chiến binh nước ngoài có thể trở về châu Âu hoặc Mỹ để thực hiện các cuộc tấn công khủng bố tại quê nhà.\r\n\r\nHôm qua, Ngoại trưởng Mỹ John Kerry cũng họp với những người đồng cấp đến từ Australia, Canada, Đan Mạch, Pháp, Đức, Italy, Ba Lan và Thổ Nhĩ Kỳ nhằm kêu gọi hỗ trợ trong cuộc chiến chống lại Nhà nước Hồi giáo. Ông Kerry sẽ tới Trung Đông tuần tới với dự tính mở rộng liên minh bên ngoài các nước phương Tây. Các quan chức Mỹ cho biết thêm Washington sẽ nêu kế hoạch cụ thể tại cuộc họp Đại hội đồng Liên Hợp Quốc vào cuối tháng này.\r\n\r\nHôm qua, Mỹ tuyên bố tiêu diệt ba thành viên cấp cao của Nhà nước Hồi giáo trong một cuộc không kích của Mỹ ở Iraq.', 3, ''),
(63, 'Về quê ăn Tết Đoan Ngọ, cả nhà 4 người chết thảm dưới gầm xe tải  ', 'Vụ tai nạn nghiêm trọng xảy ra trên quốc lộ 24B đoạn tại Km 18+100 thuộc thôn Hóc Kè (xã Ba Động, huyện Ba Tơ), vào 17h30 ngày 19/6, đã cướp đi sinh mạng của cả 1 gia đình. \r\n', '<p><span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Vụ tai nạn nghi&ecirc;m trọng xảy ra tr&ecirc;n quốc lộ 24B đoạn tại Km 18+100 thuộc th&ocirc;n H&oacute;c K&egrave; (x&atilde; Ba Động, huyện Ba Tơ), v&agrave;o 17h30 ng&agrave;y 19/6, đ&atilde; cướp đi sinh mạng của cả 1 gia đ&igrave;nh. V&agrave;o thời điểm tr&ecirc;n, xe tải chở keo mang BKS 76K-5057 do t&agrave;i xế L&ecirc; Trần Thanh B&igrave;nh (SN 1986, ngụ TP Quảng Ng&atilde;i) điều khiển, đ&atilde; t&ocirc;ng v&agrave;o xe m&aacute;y mang BKS 76K-0220 do anh L&ecirc; Văn Diệp (36 tuổi) chở theo vợ l&agrave; chị Trần Việt Nhật (30 tuổi) c&ugrave;ng 2 con nhỏ L&ecirc; Trần Tr&acirc;m Anh (7 tuổi) v&agrave; L&ecirc; Quốc Th&aacute;i (4 tuổi). Ngay sau c&uacute; t&ocirc;ng, vợ chồng anh Diệp v&agrave; chị Nhật tử vong tại chỗ, 2 ch&aacute;u nhỏ được đưa đi cấp cứu tại Bệnh viện ĐK Quảng Ng&atilde;i nhưng cũng kh&ocirc;ng qua khỏi.&nbsp;</span><br />\r\n&nbsp;</p>\r\n', 1, 'vne-1434885417.jpg'),
(64, 'Giá xăng bất ngờ tăng thêm gần 300 đồng từ 15h ', 'Từ 15h ngày hôm nay (19/6), giá xăng tăng thêm gần 300 đồng. Các loại dầu giảm nhẹ.\r\n\r\n', '<p><span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Từ 15h ng&agrave;y h&ocirc;m nay (19/6), gi&aacute; xăng tăng th&ecirc;m gần 300 đồng. C&aacute;c loại dầu giảm nhẹ. Li&ecirc;n Bộ C&ocirc;ng Thương &ndash; T&agrave;i ch&iacute;nh vừa y&ecirc;u cầu c&aacute;c doanh nghiệp xăng dầu điều chỉnh gi&aacute; c&aacute;c mặt h&agrave;ng nhi&ecirc;n liệu. Theo đ&oacute;, gi&aacute; b&aacute;n lẻ xăng RON 92, RON 95, E5 trong nước sẽ tăng th&ecirc;m 280 đồng mỗi l&iacute;t.&nbsp;C&aacute;c loại dầu giảm nhẹ. Gi&aacute; dầu diesel giảm 287 đồng/l&iacute;t, dầu hỏa giảm 275 đồng/l&iacute;t.&nbsp;Thời điểm &aacute;p dụng l&agrave; 15h h&ocirc;m nay (19/6). Theo văn bản của Bộ C&ocirc;ng Thương, gi&aacute; b&aacute;n lẻ xăng RON 92 sẽ kh&ocirc;ng cao hơn 20.711 đồng/l&iacute;t. Gi&aacute; xăng E5 kh&ocirc;ng cao hơn 20.381 đồng/l&iacute;t. Dầu diesel 0.05 S, dầu hỏa, dầu maz&uacute;t 3.5S sẽ c&oacute; gi&aacute; lần lượt kh&ocirc;ng cao hơn c&aacute;c mức 16.077 đồng/l&iacute;t, 15.099 đồng/l&iacute;t v&agrave; 12.730 đồng/kg.\\ Bảng gi&aacute; xăng dầu c&oacute; hiệu lực từ 15h ng&agrave;y 19/6. Ảnh: Petrolimex. Cơ quan điều h&agrave;nh kh&ocirc;ng thay đổi trong việc sử dụng Quỹ b&igrave;nh ổn với xăng. Mức tr&iacute;ch lập cũng như chi sử dụng Quỹ b&igrave;nh ổn cho c&aacute;c mặt h&agrave;ng xăng kho&aacute;ng kh&ocirc;ng thay đổi, giảm 98 đồng/kg với dầu mazut, xuống mức 0 đồng. Từ đầu năm đến nay, gi&aacute; xăng đ&atilde; c&oacute; 5 lần điều chỉnh với 4 lần tăng, 1 lần giảm. Thời gian qua, một số &yacute; kiến tr&aacute;i chiều li&ecirc;n quan đến Quỹ b&igrave;nh ổn đ&atilde; xuất hiện. Cơ quan điều h&agrave;nh khẳng định, việc duy tr&igrave; quỹ sẽ gi&uacute;p đảm bảo quyền lợi người ti&ecirc;u d&ugrave;ng. Trong khi đ&oacute;, TS Nguyễn Minh Phong thuộc Viện Nghi&ecirc;n cứu ph&aacute;t triển kinh tế x&atilde; hội lại cho rằng &ldquo;n&ecirc;n dẹp Quỹ b&igrave;nh ổn&rdquo;. Theo vị n&agrave;y, sự tồn tại của quỹ &ldquo;chỉ g&acirc;y nghi vấn thiếu minh bạch trong c&aacute;ch sử dụng&rdquo;. Yếu tố điều h&agrave;nh c&oacute; ảnh hưởng lớn tới việc tăng hay giảm của gi&aacute; xăng. Ảnh: Ho&agrave;ng Anh. C&ograve;n theo PGS.TS Ng&ocirc; Tr&iacute; Long &ndash; nguy&ecirc;n Viện trưởng Viện Nghi&ecirc;n cứu gi&aacute; cả (Bộ T&agrave;i ch&iacute;nh) dự đo&aacute;n, yếu tố ch&iacute;nh quyết định sự tăng hay giảm của gi&aacute; b&aacute;n lẻ xăng dầu l&agrave; do c&aacute;ch điều h&agrave;nh. Theo &ocirc;ng Long, việc dư luận li&ecirc;n tục nhắc tới chủ đề thuế bảo vệ m&ocirc;i trường l&agrave;m tăng gi&aacute; xăng c&oacute; thể sẽ t&aacute;c động tới vấn đề điều h&agrave;nh mặt&nbsp;</span><br />\r\n<br />\r\n&nbsp;</p>\r\n', 1, 'vne-1434885491.jpg'),
(65, 'Xe cẩu bể ống, hàng chục lít dầu đổ loang con đường phố cổ Hội An  ', 'Một sự cố giao thông đã xảy ra vào sáng nay 18.6, khi chiếc xe cẩu đang di chuyển trên đường thì bỗng nhiên bị bể ống dầu, khiến hàng chục lít dầu đổ tràn ra đường kéo dài hơn…\r\n', '<p><span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Một sự cố giao th&ocirc;ng đ&atilde; xảy ra v&agrave;o s&aacute;ng nay 18.6, khi chiếc xe cẩu đang di chuyển tr&ecirc;n đường th&igrave; bỗng nhi&ecirc;n bị bể ống dầu, khiến h&agrave;ng chục l&iacute;t dầu đổ tr&agrave;n ra đường k&eacute;o d&agrave;i hơn 1km tr&ecirc;n cả một con đường tại phố cổ Hội An. Th&ocirc;ng tin ban đầu, v&agrave;o khoảng 8h s&aacute;ng nay 18.6, một chiếc xe cẩu cỡ nhỏ đang di chuyển tr&ecirc;n đường Hai B&agrave; Trưng, sắp rẽ qua đường B&agrave; Triệu, TP. Hội An, tỉnh Quảng Nam th&igrave; bất ngờ bị bể ống dầu.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n', 1, 'vne-1434885605.jpg'),
(66, 'Bí mật ít người biết khiến bàn là con gà sốt tiền tỷ  ', 'Chiếc bàn là con gà đang được giới sưu tầm săn lùng, trả giá cả tỷ đồng. Bí mật bất ngờ nằm ở chất liệu đồng lạnh của món đồ này.\r\n\r\n', '<p><span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Chiếc b&agrave;n l&agrave; con g&agrave; đang được giới sưu tầm săn l&ugrave;ng, trả gi&aacute; cả tỷ đồng. B&iacute; mật bất ngờ nằm ở chất liệu đồng lạnh của m&oacute;n đồ n&agrave;y. Tr&ecirc;n thị trường, việc sưu tầm, thu mua b&agrave;n l&agrave; con g&agrave; vẫn chưa c&oacute; dấu hiệu hạ nhiệt. Nhiều năm trước đ&acirc;y, kh&ocirc;ng &iacute;t thương l&aacute;i, d&acirc;n sưu tầm cũng l&ugrave;ng sục t&igrave;m mua m&oacute;n đồ n&agrave;y. Mức gi&aacute; cho những chiếc b&agrave;n l&agrave; cổ lỗ sĩ n&agrave;y l&ecirc;n tới h&agrave;ng chục, h&agrave;ng trăm triệu đồng. Tuy nhi&ecirc;n, gần đ&acirc;y, cơn sốt mua b&agrave;n l&agrave; con g&agrave; lại tiếp tục g&acirc;y x&ocirc;n xao v&igrave; mức gi&aacute; cho m&oacute;n đồ n&agrave;y đ&atilde; l&ecirc;n tới cả tỷ, thậm ch&iacute; chục tỷ đồng. Trong đ&oacute;, loại b&agrave;n l&agrave; con g&agrave; được c&aacute;c l&aacute;i thương t&igrave;m mua l&agrave; loại b&agrave;n l&agrave; than c&oacute; kh&oacute;a con g&agrave; bằng đồng. Mỗi b&ecirc;n b&agrave;n l&agrave; c&oacute; 12 lỗ th&ocirc;ng hơi tr&ograve;n, tổng cộng 2 b&ecirc;n l&agrave; 24 lỗ. Điều kiện để c&aacute;c l&aacute;i thương &ldquo;ng&oacute;&rdquo; đến chiếc b&agrave;n l&agrave; cổ lỗ n&agrave;y ch&iacute;nh l&agrave; chiếc kh&oacute;a tr&ecirc;n b&agrave;n l&agrave; h&igrave;nh con g&agrave; phải l&agrave;m bằng đồng lạnh. T&ugrave;y theo c&acirc;n nặng của chiếc kh&oacute;a n&agrave;y m&agrave; mức gi&aacute; thu mua sẽ kh&aacute;c nhau.</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Đồng lạnh l&agrave; một loại vật liệu được d&acirc;n sưu tầm đồ cổ thường xuy&ecirc;n đăng tin t&igrave;m mua. Trong đ&oacute;, c&aacute;c tin đăng tải thu mua đồng lạnh đều đi k&egrave;m những ti&ecirc;u chuẩn, dấu hiệu nhận biết đồng lạnh đ&aacute;ng gi&aacute;. Như phải nặng so với đồng b&igrave;nh thường 3-4 lần. Khi nung trong bếp ga khoảng 1 giờ, khi bỏ ra, lấy c&acirc;y nến (đ&egrave;n cầy) d&iacute; v&agrave;o, nếu c&acirc;y nến kh&ocirc;ng chảy th&igrave; đ&oacute; l&agrave; đồng lạnh. Ngo&agrave;i ra, người b&aacute;n c&oacute; thể tự kiểm tra m&oacute;n đồ của m&igrave;nh c&oacute; đ&uacute;ng l&agrave; đồng lạnh hay kh&ocirc;ng bằng c&aacute;ch d&ugrave;ng b&igrave;nh kh&ograve; ga kh&ograve; trực tiếp &iacute;t nhất 40 ph&uacute;t rồi tiếp tục d&ugrave;ng nến thử như tr&ecirc;n, nến kh&ocirc;ng chảy l&agrave; đạt. D&acirc;n mua b&aacute;n đồ cổ cũng kh&aacute;o nhau c&aacute;ch ph&acirc;n biệt kh&oacute;a h&igrave;nh con g&agrave; c&oacute; đ&uacute;ng l&agrave; đồng lạnh hay kh&ocirc;ng bằng c&aacute;ch đun s&ocirc;i nước (tỷ lệ 200 ml nước/1g đồng). Nước đang s&ocirc;i thả con g&agrave; v&agrave;o sẽ tắt s&ocirc;i v&agrave; đun tiếp trong 30 ph&uacute;t, nước s&ocirc;i lại v&agrave; c&oacute; thể th&ograve; tay v&agrave;o lấy con g&agrave; ra. Ngo&agrave;i ra, nếu chiếc kh&oacute;a h&igrave;nh con g&agrave; tr&ecirc;n b&agrave;n l&agrave; kh&ocirc;ng l&agrave; đồng lạnh m&agrave; l&agrave; đồng đổi m&agrave;u th&igrave; gi&aacute; trị của ch&uacute;ng giảm đi một nửa. Mỗi kg đồng đổi m&agrave;u được rao thu mua từ 2-4 tỷ đồng, t&ugrave;y v&agrave;o thời gian đổi m&agrave;u về b&igrave;nh thường của đồng. Đồng lạnh thường c&oacute; trong c&aacute;c đồ cổ, đồ thờ c&uacute;ng, con g&agrave; ở tr&ecirc;n c&aacute;c b&agrave;n l&agrave; than ng&agrave;y xưa n&ecirc;n kh&ocirc;ng kh&oacute; hiểu nhiều người sưu tầm đăng tin, l&ugrave;ng sục t&igrave;m mua chiếc b&agrave;n l&agrave; xưa cũ n&agrave;y. Chưa r&otilde; d&acirc;n thu mua, người sưu tầm t&igrave;m đồng lạnh để l&agrave;m g&igrave;, song mức gi&aacute; cho mỗi kg đồng kh&ocirc;ng hề rẻ, khoảng 5 tỷ đồng. Bản th&acirc;n người b&aacute;n cũng t&ograve; m&ograve; v&agrave; kh&ocirc;ng r&otilde; c&aacute;ch ph&acirc;n biệt đồng lạnh trong m&oacute;n đồ m&agrave; m&igrave;nh sở hữu.</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Mặc d&ugrave; theo c&aacute;ch thu mua, &ldquo;t&acirc;ng&rdquo; gi&aacute; của d&acirc;n bu&ocirc;n b&aacute;n th&igrave; chất liệu đồng lạnh l&agrave;m kh&oacute;a h&igrave;nh con g&agrave; mang lại nguồn lợi cực lớn, song việc nhận biết ch&uacute;ng kh&ocirc;ng hẳn dễ d&agrave;ng v&agrave; tin cậy. Trong d&ograve;ng b&agrave;n l&agrave; đồng c&oacute; kh&oacute;a h&igrave;nh con g&agrave;, c&oacute; đến ba loại dễ nhận biết v&agrave; c&oacute; tổng cộng hơn hai mươi loại b&agrave;n ủi nh&igrave;n kh&aacute; giống nhau. Thậm ch&iacute; mẫu m&atilde; giống y hệt nhau, t&iacute;nh năng v&agrave; dấu hiệu cũng giống hệt nhau nhưng tr&ecirc;n thực tế về mặt chất lượng th&igrave; kh&aacute;c nhau cả một trời một vực. Những người sưu tầm đồ cổ l&acirc;u năm cho biết, chiếc b&agrave;n l&agrave; con g&agrave; do Ph&aacute;p sản xuất v&agrave; chiếc b&agrave;n con g&agrave; của Huế sản xuất những năm 1930 giống hệt nhau, thậm ch&iacute;, trọng lượng, chất lượng cũng kh&oacute; l&ograve;ng ph&acirc;n biệt. Trong trường hợp người mua kh&ocirc;ng c&oacute; kinh nghiệm, c&oacute; thể mua phải b&agrave;n l&agrave; do Huế sản xuất. Loại b&agrave;n l&agrave; n&agrave;y kh&ocirc;ng thể gi&aacute; trị bằng b&agrave;n l&agrave; do Ph&aacute;p sản xuất. Người b&aacute;n cũng kh&ocirc;ng biết m&oacute;n đồ của m&igrave;nh c&oacute; gi&aacute; trị thật hay kh&ocirc;ng. Việc n&agrave;y dễ khiến việc mua b&aacute;n m&oacute;n đồ tiềm ẩn c&aacute;c nguy cơ rủi ro do số tiền được rao mua b&agrave;n l&agrave; qu&aacute; lớn, h&agrave;ng trăm thậm ch&iacute; cả tỷ đồng. Trước đ&oacute;, những vụ mua b&aacute;n đồng lạnh, đồng đổi m&agrave;u cũng từng xảy ra kh&ocirc;ng &iacute;t rủi ro, lừa đảo. Thiết nghĩ, người mua cũng kh&ocirc;ng n&ecirc;n qu&aacute; tin v&agrave;o c&aacute;c tin rao cả tỷ đồng kh&ocirc;ng c&oacute; g&igrave; chắc chắn đ&oacute;.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n', 1, 'vne-1434885753.jpg'),
(67, 'Hà Nội sắp phạt người nói tục, hành vi thiếu văn hóa nơi công cộng  ', 'Sở Văn hóa Thể thao và Du lịch Hà Nội đang khảo sát nạn nói tục, hành vi thiếu văn hóa nơi công cộng.\r\n', '<p><span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Sở Văn h&oacute;a Thể thao v&agrave; Du lịch H&agrave; Nội đang khảo s&aacute;t nạn n&oacute;i tục, h&agrave;nh vi thiếu văn h&oacute;a nơi c&ocirc;ng cộng. &Ocirc;ng Trần Tuấn Anh, Ch&aacute;nh Văn ph&ograve;ng Sở Văn h&oacute;a Thể thao v&agrave; Du lịch H&agrave; Nội cho biết, Sở đang chủ tr&igrave; x&acirc;y dựng quy tắc ứng xử trong cơ quan, đơn vị cộng đồng, trong đ&oacute; c&oacute; khảo s&aacute;t nội dung về n&oacute;i tục nơi c&ocirc;ng cộng. Đề &aacute;n do Tiến sỹ Mai Anh chủ tr&igrave;. Theo đ&oacute;, Dự thảo Bộ khung quy tắc ứng xử đưa ra c&aacute;c ti&ecirc;u ch&iacute; cụ thể với 6 nh&oacute;m gồm: cơ quan h&agrave;nh ch&iacute;nh sự nghiệp, trường học, bệnh viện, doanh nghiệp, khu d&acirc;n cư v&agrave; nơi c&ocirc;ng cộng.</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 0); font-family:arial; font-size:12px">Với mỗi nh&oacute;m sẽ c&oacute; những quy tắc ri&ecirc;ng, v&iacute; dụ nh&oacute;m người d&acirc;n nơi c&ocirc;ng cộng cần: Chấp h&agrave;nh nội quy, quy định; T&ocirc;n trọng, th&acirc;n thiện; Văn minh, lịch sự; Đo&agrave;n kết, chia sẻ; Bảo vệ di t&iacute;ch lịch sử, cảnh quan, m&ocirc;i trường; &Yacute; thức ph&ecirc; ph&aacute;n những h&agrave;nh vi vi phạm quy định. Với nh&oacute;m doanh nghiệp, cần: T&ocirc;n trọng đạo đức kinh doanh; Coi trọng chữ t&iacute;n; C&ocirc;ng bằng; T&ocirc;n trọng; Chia sẻ lợi &iacute;ch v&agrave; tr&aacute;ch nhiệm x&atilde; hội. &Ocirc;ng T&ocirc; Văn Động, Gi&aacute;m đốc Sở Văn h&oacute;a cho biết, Bộ quy tắc ứng xử kh&ocirc;ng phải văn bản quy phạm, n&ecirc;n nếu n&oacute;i bắt buộc thực hiện th&igrave; kh&ocirc;ng đ&uacute;ng. Tuy nhi&ecirc;n, c&aacute;c giai đoạn tiếp theo của đề &aacute;n sẽ đưa ra những chế t&agrave;i với c&aacute;c h&agrave;nh vi ứng xử kh&ocirc;ng đ&uacute;ng quy tắc, v&iacute; dụ vứt r&aacute;c ra đường, n&oacute;i tục&hellip; Trước đ&oacute;, th&agrave;nh phố đ&atilde; nhận được phản &aacute;nh về t&igrave;nh trạng một số bạn trẻ, nghệ sỹ n&oacute;i th&ocirc; tục nơi c&ocirc;ng cộng. Ph&oacute; chủ tịch UBND TP H&agrave; Nội L&ecirc; Hồng Sơn&nbsp;đ&atilde; giao Sở Văn h&oacute;a &ndash; thể thao v&agrave; du lịch, Sở Gi&aacute;o dục &ndash; đ&agrave;o tạo v&agrave; UBND c&aacute;c quận huyện, thị x&atilde; kiểm tra, xem x&eacute;t, c&oacute; biện ph&aacute;p xử l&yacute; nhằm hạn chế cao nhất những h&agrave;nh vi thiếu văn h&oacute;a trong nh&agrave; trường v&agrave; ngo&agrave;i x&atilde; hội. Theo đ&oacute;, từ cuối năm 2014, theo chỉ đạo của UBND TP, Sở VH-TT&amp;DL H&agrave; Nội đ&atilde; tổ chức c&aacute;c cuộc hội thảo lấy &yacute; kiến x&acirc;y dựng &ldquo;hệ thống quy tắc ứng xử trong cơ quan, đơn vị, cộng đồng d&acirc;n cư TP H&agrave; Nội&rdquo; dự kiến ban h&agrave;nh trong năm 2015. Từ th&aacute;ng 5/2014, tại quy chế thực hiện kỷ cương h&agrave;nh ch&iacute;nh v&agrave; văn h&oacute;a c&ocirc;ng sở tại Văn ph&ograve;ng UBND th&agrave;nh phố H&agrave; Nội đ&atilde; c&oacute; chỉ đạo&nbsp;cấm c&ocirc;ng chức n&oacute;i tục. Theo Quy chế, trong thực thi c&ocirc;ng vụ, nhiệm vụ, c&aacute;n bộ c&ocirc;ng chức phải c&oacute; t&aacute;c phong chuy&ecirc;n nghiệp, hiện đại; c&oacute; th&aacute;i độ h&ograve;a nh&atilde;, lịch sự, t&ocirc;n trọng trong ứng xử, giao tiếp. Trong giao tiếp, ứng xử với đồng nghiệp phải c&oacute; th&aacute;i độ lịch sự, t&ocirc;n trọng, trung thực, th&acirc;n thiện, hợp t&aacute;c. Ng&ocirc;n ngữ giao tiếp phải chuẩn mực r&otilde; r&agrave;ng; kh&ocirc;ng n&oacute;i tục, tiếng l&oacute;ng, qu&aacute;t nạt, phải lắng nghe &yacute; kiến của đồng nghiệp&hellip;</span><br />\r\n&nbsp;</p>\r\n', 1, 'vne-1434885868.jpg'),
(68, 'Lời trăng trối của nam sinh qua đời trước ngày tốt nghiệp', '"Hãy ôm tớ thật chặt lần cuối trước khi cậu bước đi nhé!" là dòng tâm sự của Nguyễn Đức Huy gửi cho một người đặc biệt trên Facebook.', '<p><span style="color:rgb(0, 0, 0); font-family:times new roman,times,serif; font-size:16.2399997711182px">Ng&agrave;y 29/6, th&ocirc;ng tin về Nguyễn Đức Huy - nam sinh khoa Kiến tr&uacute;c, Đại học Văn Lang TP HCM - được đặc c&aacute;ch trao bằng tốt nghiệp d&ugrave; đ&atilde; qua đời khiến cộng đồng mạng x&uacute;c động.&nbsp;Sau 5 năm học tập, Huy đ&atilde; ho&agrave;n th&agrave;nh luận &aacute;n tốt nghiệp. Tuy nhi&ecirc;n, c&aacute;ch đ&acirc;y một th&aacute;ng, khi chưa kịp nhận th&agrave;nh quả của m&igrave;nh, Huy bất ngờ qua đời v&igrave; đột quỵ.</span></p>\r\n\r\n<p><span style="color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:12px">Trong buổi lễ tốt nghiệp diễn ra ng&agrave;y 28/6 vừa qua, bố của Huy đ&atilde; đại diện gia đ&igrave;nh l&ecirc;n nhận tấm bằng Đại học ch&iacute;nh quy loại kh&aacute; từ tay thầy hiệu trưởng. Trước sự kiện n&agrave;y, d&acirc;n mạng ho&agrave;n to&agrave;n ủng hộ quyết định của nh&agrave; trường khi c&ocirc;ng nhận chức danh kiến tr&uacute;c sư đối với Huy, nhằm tr&acirc;n trọng sự nỗ lực v&agrave; th&agrave;nh quả học tập của nam sinh. Tr&ecirc;n Facebook của Nguyễn Đức Huy, nhiều bạn b&egrave; b&agrave;y tỏ sự thương tiếc d&agrave;nh cho cậu bạn xấu số n&agrave;y. Đặc biệt, những d&ograve;ng chia sẻ cuối c&ugrave;ng từ Huy nhận được sự quan t&acirc;m, đồng cảm từ mọi người. Nguyễn Đức Huy l&agrave; sinh vi&ecirc;n hiền l&agrave;nh, c&oacute; nụ cười ph&uacute;c hậu. Ảnh: FBNV. Trong d&ograve;ng trạng th&aacute;i d&agrave;nh ri&ecirc;ng cho một người đặc biệt, Huy cho biết:&nbsp;&quot;Hãy &ocirc;m tớ th&acirc;̣t chặt m&ocirc;̣t l&acirc;̀n cu&ocirc;́i trước khi c&acirc;̣u bước đi nhé!&nbsp;Mình chọn vi&ecirc;̣c ra đi, mình kh&ocirc;ng th&ecirc;̉ là người y&ecirc;u của c&acirc;̣u, kh&ocirc;ng có nghĩa là mình kh&ocirc;ng y&ecirc;u c&acirc;̣u đ&acirc;u đ&acirc;́y&quot;. Bạn b&egrave; của Huy chia sẻ, họ kh&ocirc;ng ngờ rằng những lời n&oacute;i đầy ẩn &yacute; n&agrave;y lại b&aacute;o trước về sự ra đi của ch&agrave;ng trai chỉ sau đ&oacute; &iacute;t ng&agrave;y. Ng&agrave;y 2/6, Nguyễn Đức Huy bị đột quỵ sau khi ho&agrave;n th&agrave;nh b&agrave;i tốt nghiệp khoa Kiến tr&uacute;c v&agrave; trước thời điểm bảo vệ luận &aacute;n 5 ng&agrave;y.</span><br />\r\n<br />\r\n<br />\r\n<a href="http://news.zing.vn/" style="box-sizing: border-box; margin: 0px; padding: 0px; font-size: 0px; vertical-align: baseline; text-decoration: none; color: rgb(51, 51, 51); font-family: Arial, sans-serif; display: block; line-height: 0; background: transparent;" target="_blank" title="Zing News">Nguồn Zing News</a></p>\r\n\r\n<div>&nbsp;</div>\r\n', 1, 'vne-1435647589.jpg'),
(69, 'Dùng xe tải đưa thí sinh đến địa điểm thi', 'Sáng nay, nhiều thí sinh đến trường thi từ 5h30 để tránh nắng nóng, dù 8h mới làm thủ tục dự thi. Hai xe tải được huy động đưa thí sinh đến cụm thi Đại học Bách Khoa Hà Nội.', '<p><strong>S&aacute;ng nay, nhiều th&iacute; sinh đến trường thi từ 5h30 để tr&aacute;nh nắng n&oacute;ng, d&ugrave; 8h mới l&agrave;m thủ tục dự thi. Hai xe tải được huy động đưa th&iacute; sinh đến cụm thi Đại học B&aacute;ch Khoa H&agrave; Nội.</strong></p>\r\n\r\n<p><strong>S&aacute;ng nay, nhiều th&iacute; sinh đến trường thi từ 5h30 để tr&aacute;nh nắng n&oacute;ng, d&ugrave; 8h mới l&agrave;m thủ tục dự thi. Hai xe tải được huy động đưa th&iacute; sinh đến cụm thi Đại học B&aacute;ch Khoa H&agrave; Nội.</strong></p>\r\n', 1, 'vne-1435647666.jpg'),
(70, 'Hoa tam giác mạch nở bừng sáng Simacai giữa mùa hè', 'Không cần chờ đến cuối thu, ngay những ngày cuối tháng 6, bạn đã có thể đắm mình trong sắc tím hồng rực rỡ của những vạt hoa tam giác mạch nở trái mùa ở Lử Thẩn (Lào Cai).', '<p><strong>Kh&ocirc;ng cần chờ đến cuối thu, ngay những ng&agrave;y cuối th&aacute;ng 6, bạn đ&atilde; c&oacute; thể đắm m&igrave;nh trong sắc t&iacute;m hồng rực rỡ của những vạt hoa tam gi&aacute;c mạch nở tr&aacute;i m&ugrave;a ở Lử Thẩn (L&agrave;o Cai).</strong></p>\r\n\r\n<p><strong>Kh&ocirc;ng cần chờ đến cuối thu, ngay những ng&agrave;y cuối th&aacute;ng 6, bạn đ&atilde; c&oacute; thể đắm m&igrave;nh trong sắc t&iacute;m hồng rực rỡ của những vạt hoa tam gi&aacute;c mạch nở tr&aacute;i m&ugrave;a ở Lử Thẩn (L&agrave;o Cai).</strong></p>\r\n', 1, 'vne-1435648125.jpg'),
(71, 'Vì sao sát nhân vượt ngục qua mặt cảnh sát Mỹ suốt 23 ngày?', 'Richard Matt và David Sweat, 2 phạm nhân trốn khỏi nhà tù an ninh bậc nhất nước Mỹ, có thể đã dùng hạt tiêu để qua mặt chó nghiệp vụ và lẩn trốn trong suốt hơn 3 tuần.', '<p><span style="color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:12px">Giới điều tra t&igrave;m thấy một hộp hạt ti&ecirc;u tại trại m&agrave; Richard Matt v&agrave; David Sweat, 2 kẻ giết người tẩu tho&aacute;t khỏi nh&agrave; giam Clinton ở New York, Mỹ, c&oacute; thể đ&atilde; dừng ch&acirc;n. V&igrave; vậy, theo Joseph D&rsquo;Amico, sĩ quan cảnh s&aacute;t bang New York, c&oacute; thể 2 s&aacute;t nh&acirc;n đ&atilde; rải hạt ti&ecirc;u để đ&aacute;nh lừa ch&oacute; nghiệp vụ. M&aacute;nh kh&oacute;e 2 phạm nh&acirc;n qua mặt cảnh s&aacute;t trong qu&aacute; tr&igrave;nh trốn chạy từng được đề cập trong bộ phim Cool Hand Luke năm 1967. &ldquo;Ch&uacute;ng t&ocirc;i gặp kh&oacute; khăn trong việc t&igrave;m dấu vết của Matt v&agrave; Sweat v&igrave; c&aacute;ch m&agrave; ch&uacute;ng &aacute;p dụng kh&aacute; hiệu quả. Nếu bạn viết kịch bản cho phim th&igrave; vở kịch n&agrave;y rất ho&agrave;n hảo&rdquo;, D Amico n&oacute;i với The New York Times. Mỹ tiến h&agrave;nh một cuộc truy l&ugrave;ng 2 kẻ trốn chạy trong chiến dịch quy m&ocirc; lớn, từ Pennsylvania tới bi&ecirc;n giới Canada. Ch&iacute;nh quyền đ&atilde; huy động 1.300 người tới c&aacute;c khu rừng ở miền Bắc New York v&agrave; kiểm tra nghi&ecirc;m ngặt tại c&aacute;c chốt an ninh. Qu&aacute; tr&igrave;nh đuổi bắt kết th&uacute;c trưa 28/6 khi một cảnh s&aacute;t bắn v&agrave; bắt sống Sweat ở Constable, New York. Y được đưa tới bệnh viện trong t&igrave;nh trạng nguy cập. Bạn t&ugrave; của Sweat, Matt, bị ti&ecirc;u diệt khi đang cầm s&uacute;ng đối mặt với nh&agrave; chức tr&aacute;ch ng&agrave;y 26/6. &ldquo;Cơn &aacute;c mộng đ&atilde; qua&rdquo;, Andrew Cuomo, Thống đốc bang New York n&oacute;i tại một cuộc họp b&aacute;o.</span><br />\r\n<span style="color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:12px">Matt v&agrave; Sweat trốn khỏi trại cải tạo Clinton, nơi c&aacute;ch vị tr&iacute; ch&uacute;ng sa lưới khoảng 50 km, bằng nhiều dụng cụ điện ng&agrave;y 6/6. Nh&agrave; chức tr&aacute;ch cho rằng, 2 phạm nh&acirc;n ở c&aacute;c ph&ograve;ng giam liền kề nhau trong nh&agrave; t&ugrave; v&agrave; ch&uacute;ng để quần &aacute;o l&ecirc;n giường lừa quản ngục khi đ&agrave;o tẩu. Ch&uacute;ng c&ograve;n để lại mảnh giấy đầy th&aacute;ch thức với khu&ocirc;n mặt cười v&agrave; d&ograve;ng chữ &ldquo;Ch&uacute;c một ng&agrave;y tốt l&agrave;nh&rdquo;. Matt v&agrave; bạn t&ugrave; đ&atilde; d&ugrave;ng lời lẽ ngon ngọt, dụ nh&acirc;n vi&ecirc;n nh&agrave; t&ugrave; tuồn đục v&agrave; c&aacute;c dụng cụ kh&aacute;c để gi&uacute;p ch&uacute;ng vượt ra khỏi h&agrave;ng r&agrave;o an ninh nh&agrave; t&ugrave; v&agrave; tho&aacute;t ra ngo&agrave;i. Nh&agrave; điều tra buộc tội 2 nh&acirc;n vật hỗ trợ t&ugrave; nh&acirc;n lẩn trốn l&agrave; Joyce Mitchell, người l&agrave;m trong xưởng may b&ecirc;n trong Clinton, v&agrave; nh&acirc;n vi&ecirc;n nh&agrave; t&ugrave; Gene Palmer. Palmer sẽ hầu t&ograve;a v&igrave; tội tuồn h&agrave;ng lậu v&agrave;o trại giam v&agrave; h&agrave;nh động kh&ocirc;ng đ&uacute;ng nhiệm vụ. Người n&agrave;y đưa thịt đ&ocirc;ng lạnh chứa lưỡi cưa v&agrave; tua v&iacute;t v&agrave;o Clinton. B&agrave; Mitchell đ&atilde; hầu t&ograve;a v&agrave; phủ nhận c&aacute;o buộc của t&ograve;a &aacute;n về c&aacute;c tội li&ecirc;n quan đến vụ vượt ngục chấn động nước Mỹ. Trong một diễn biến mới, Sweat đ&atilde; khai nhận những th&ocirc;ng tin đầu ti&ecirc;n về &acirc;m mưu tẩu tho&aacute;t t&aacute;o tợn c&aacute;ch đ&acirc;y 3 tuần, CNN đưa tin. Cuomo tiết lộ một số th&ocirc;ng tin trong bi&ecirc;n bản lời khai của Sweat rằng, y v&agrave; đồng phạm Richard Matt ban đầu dự định chạy trốn sang Mexico. Tuy nhi&ecirc;n, sau khi tẩu tho&aacute;t từ trại giam Clinton, nữ nh&acirc;n vi&ecirc;n nh&agrave; t&ugrave; Joyce Mitchell kh&ocirc;ng xuất hiện như kế hoạch để l&aacute;i xe chở bọn ch&uacute;ng. Hai kẻ giết người vượt ngục khi Sweat đang thụ &aacute;n chung th&acirc;n kh&ocirc;ng &acirc;n x&aacute; v&igrave; giết một cảnh s&aacute;t năm 2002 c&ograve;n Matt chịu mức &aacute;n 25 năm v&igrave; giết &ocirc;ng chủ cũ.</span><br />\r\n<br />\r\n<br />\r\n<br />\r\n<a href="http://news.zing.vn/" style="box-sizing: border-box; margin: 0px; padding: 0px; font-size: 0px; vertical-align: baseline; text-decoration: none; color: rgb(51, 51, 51); font-family: Arial, sans-serif; display: block; line-height: 0; background: transparent;" target="_blank" title="Zing News">Nguồn Zing News</a></p>\r\n\r\n<div>&nbsp;</div>\r\n\r\n<p><br />\r\n<a href="http://news.zing.vn/" style="box-sizing: border-box; margin: 0px; padding: 0px; font-size: 0px; vertical-align: baseline; text-decoration: none; color: rgb(51, 51, 51); font-family: Arial, sans-serif; display: block; line-height: 0; background: transparent;" target="_blank" title="Zing News">Nguồn Zing News</a></p>\r\n\r\n<div>&nbsp;</div>\r\n', 3, 'vne-1435648753.jpeg'),
(72, 'Cháy trên tàu siêu tốc Nhật Bản, 2 người chết', 'Giới chức Nhật Bản cho biết hai người đã chết, 20 người bị bỏng sau một vụ tự thiêu trên chuyến tàu cao tốc từ Tokyo tới Osaka sáng 30/6.', '<p><span style="color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:12px">Ryo Iwasaki, quan chức cứu hỏa th&agrave;nh phố Odawara, cho biết, vụ tự thi&ecirc;u xảy ra gần khu vực nh&agrave; vệ sinh đo&agrave;n t&agrave;u. Sau khi ph&aacute;t hiện sự việc, đo&agrave;n t&agrave;u buộc phải dừng lại ở th&agrave;nh phố Odawara, t&acirc;y nam Tokyo. Theo Iwasaki m&ocirc; tả, 2 nạn nh&acirc;n được ph&aacute;t hiện trong t&igrave;nh trạng &quot;tim ngừng đập&quot;. Đ&acirc;y l&agrave; thuật ngữ m&agrave; nh&agrave; chức tr&aacute;ch Nhật Bản sử dụng để m&ocirc; tả t&igrave;nh trạng ngừng thở v&agrave; mất mạch của con người trong trường hợp b&aacute;c sĩ chưa tuy&ecirc;n bố họ đ&atilde; chết. Sự cố l&agrave;m 2 người kh&aacute;c tr&ecirc;n t&agrave;u bị thương nặng v&agrave; 20 người bị bỏng nhẹ, NHK dẫn lời cảnh s&aacute;t Nhật Bản cho biết. Ngay sau khi nhận tin b&aacute;o, xe cứu hỏa th&agrave;nh phố Odawara được huy động tới hiện trường. Đ&agrave;i truyền h&igrave;nh NHK ph&aacute;t đi h&igrave;nh ảnh cho thấy l&iacute;nh cứu hỏa chạy tới t&agrave;u, nơi h&agrave;nh kh&aacute;ch vẫn đang mắc kẹt b&ecirc;n trong. Vụ việc khiến tuyến đường sắt Tokyo &ndash; Osaka bị đ&igrave;nh chuyến ở cả hai chiều.</span><br />\r\n<br />\r\n<a href="http://news.zing.vn/" style="box-sizing: border-box; margin: 0px; padding: 0px; font-size: 0px; vertical-align: baseline; text-decoration: none; color: rgb(51, 51, 51); font-family: Arial, sans-serif; display: block; line-height: 0; background: transparent;" target="_blank" title="Zing News">Nguồn Zing News</a></p>\r\n\r\n<div>&nbsp;</div>\r\n', 3, 'vne-1435649967.jpg'),
(73, 'Máy bay Indonesia rơi, ít nhất 30 người chết', 'Ít nhất 30 người thiệt mạng khi một máy bay vận tải Hercules C-130 của Không quân Indonesia rơi xuống khu dân cư tại thành phố Medan, đảo Sumatra, ngày 30/6.', '<p><strong>&Iacute;t nhất 30 người thiệt mạng khi một m&aacute;y bay vận tải Hercules C-130 của Kh&ocirc;ng qu&acirc;n Indonesia rơi xuống khu d&acirc;n cư tại th&agrave;nh phố Medan, đảo Sumatra, ng&agrave;y 30/6.</strong></p>\r\n\r\n<p><strong>&Iacute;t nhất 30 người thiệt mạng khi một m&aacute;y bay vận tải Hercules C-130 của Kh&ocirc;ng qu&acirc;n Indonesia rơi xuống khu d&acirc;n cư tại th&agrave;nh phố Medan, đảo Sumatra, ng&agrave;y 30/6.</strong></p>\r\n', 1, 'vne-1435650033.jpg');
INSERT INTO `news` (`id_news`, `name`, `preview_text`, `detail_text`, `id_cat`, `picture`) VALUES
(74, 'Vua phá lưới Lao League xứng đáng được Miura trao cơ hội', 'HLV Phan Thanh Hùng nhận xét, ở độ tuổi 21, hiếm cầu thủ thể hiện được phong độ ấn tượng như tiền đạo Nguyễn Xuân Nam tại giải VĐQG Lào.', '<p>Người thầy của Xu&acirc;n Nam ở CLB H&agrave; Nội T&amp;T b&agrave;y tỏ cảm gi&aacute;c phấn khởi khi hay tin cậu học tr&ograve; dẫn đầu danh s&aacute;ch vua ph&aacute; lưới Lao League bằng phong độ l&agrave;m b&agrave;n thuyết phục.</p>\r\n\r\n<p>Trao đổi c&ugrave;ng&nbsp;<em>Zing.vn</em>, HLV Phan Thanh H&ugrave;ng cho biết: &ldquo;Xu&acirc;n Nam sở hữu nhiều tố chất để trở th&agrave;nh tiền đạo giỏi. Chiều cao của cậu ta kh&aacute; l&yacute; tưởng (1,78 m), kỹ thuật to&agrave;n diện. B&ecirc;n cạnh đ&oacute; l&agrave; tốc độ v&agrave; sự nhạy b&eacute;n trước khung th&agrave;nh đối phương&rdquo;.</p>\r\n\r\n<p><span style="color:rgb(0, 0, 0); font-family:arial,sans-serif; font-size:12px">Tr&ecirc;n thực tế, ch&iacute;nh nh&agrave; cầm qu&acirc;n người Đ&agrave; Nẵng l&agrave; người đ&atilde; đề xuất đưa Xu&acirc;n Nam từ đội hạng Nhất H&agrave; Nội l&ecirc;n H&agrave; Nội T&amp;T thi đấu ở V.League. Nhưng khi thấy cựu vua ph&aacute; lưới U19 ĐNA c&oacute; dấu hiệu bị thui chột với việc ngồi dự bị qu&aacute; l&acirc;u, đội b&oacute;ng thủ đ&ocirc; đ&atilde; quyết định gửi anh sang L&agrave;o để lấy lại phong độ. &ldquo;Ban đầu, t&ocirc;i nghĩ Xu&acirc;n Nam cần th&ecirc;m một, hai m&ugrave;a giải để cứng c&aacute;p hơn. Nhưng phong độ của cậu ta khiến tất cả đều cảm thấy bất ngờ&rdquo;, HLV Phan Thanh H&ugrave;ng chia sẻ. Theo &ocirc;ng H&ugrave;ng, ở độ tuổi 21 v&agrave; bị đặt v&agrave;o m&ocirc;i trường ho&agrave;n to&agrave;n xa lạ, rất kh&oacute; để bất kỳ cầu thủ n&agrave;o đ&aacute; tốt. &ldquo;T&ocirc;i kh&ocirc;ng nghĩ l&agrave; mức độ cạnh tranh của Lao League k&eacute;m quyết liệt. Với đội h&igrave;nh gồm 4 ngoại binh, kh&ocirc;ng dễ để Xu&acirc;n Nam vượt qua những đồng nghiệp đến từ Nam Mỹ hay ch&acirc;u Phi tr&ecirc;n danh s&aacute;ch l&agrave;m b&agrave;n&rdquo;, HLV của H&agrave; Nội T&amp;T n&oacute;i. V&igrave; vậy, nh&agrave; cầm qu&acirc;n người Đ&agrave; Nẵng đ&aacute;nh gi&aacute;: &ldquo;T&ocirc;i kh&ocirc;ng muốn can thiệp v&agrave;o c&ocirc;ng việc của &ocirc;ng Miura. Nhưng Xu&acirc;n Nam xứng đ&aacute;ng được trao cơ hội tại U23 Việt Nam, &iacute;t nhất l&agrave; thử sức khi tập trung đội dự tuyển&rdquo;. Cơ hội d&agrave;nh cho tiền đạo sinh năm 1994 sắp tới sẽ rộng mở hơn khi U23 Việt Nam chuẩn bị cho VCK U23 ch&acirc;u &Aacute;. Tại giải đấu n&agrave;y, do giới hạn về độ tuổi, HLV Miura sẽ kh&ocirc;ng c&oacute; sự phục vụ của lứa cầu thủ sinh năm 1992 như Hồng Qu&acirc;n, Ho&agrave;ng Thịnh, Huy H&ugrave;ng hay Phi Sơn&hellip; Thi đấu thăng hoa nhưng Xu&acirc;n Nam vẫn kh&ocirc;ng ngu&ocirc;i nỗi buồn v&igrave; kh&ocirc;ng được trao cơ hội thử sức tại U23 Việt Nam. Trước th&ocirc;ng tin b&oacute;ng đ&aacute; L&agrave;o muốn nhập tịch cho tiền đạo Xu&acirc;n Nam, Chủ tịch CLB H&agrave; Nội T&amp;T - &ocirc;ng Nguyễn Quốc Hội ph&aacute;t biểu: &ldquo;Xu&acirc;n Nam l&agrave; thế hệ tương lai của CLB H&agrave; Nội T&amp;T. Ch&uacute;ng t&ocirc;i vẫn theo d&otilde;i s&aacute;t qu&aacute; tr&igrave;nh tiến bộ của cậu ta. Nếu Xu&acirc;n Nam đ&aacute; tốt, kh&ocirc;ng c&oacute; l&yacute; do g&igrave; cậu ta kh&ocirc;ng được trao cơ hội ở V.League&rdquo;. Khi Xu&acirc;n Nam về nước trong qu&atilde;ng thời gian Lao League tạm nghỉ tr&aacute;nh SEA Games 28, đ&iacute;ch th&acirc;n &ocirc;ng Hội đ&atilde; đặt v&eacute; để anh tham gia chuyến tập huấn tại Đ&agrave; Nẵng c&ugrave;ng đội b&oacute;ng thủ đ&ocirc;. Về phần m&igrave;nh, tiền đạo đang dẫn đầu danh s&aacute;ch l&agrave;m b&agrave;n của Lao League khẳng định: &ldquo;T&ocirc;i sẽ kh&ocirc;ng nhập tịch để thi đấu cho tuyển L&agrave;o. Mục ti&ecirc;u của t&ocirc;i l&agrave; chơi tốt ở đ&acirc;y, sau đ&oacute; về nước phục vụ cho H&agrave; Nội T&amp;T v&agrave; thực hiện mơ ước kho&aacute;c &aacute;o U23 Việt Nam&rdquo;.</span><br />\r\n<br />\r\n<br />\r\n<a href="http://news.zing.vn/" style="box-sizing: border-box; margin: 0px; padding: 0px; font-size: 0px; vertical-align: baseline; text-decoration: none; color: rgb(51, 51, 51); font-family: Arial, sans-serif; display: block; line-height: 0; background: transparent;" target="_blank" title="Zing News">Nguồn Zing News</a></p>\r\n\r\n<div>&nbsp;</div>\r\n', 31, 'vne-1435650634.jpg'),
(75, 'Công Phượng, HLV Miura và HA Gia Lai', 'Công Phượng chơi rất hay trong màu áo U23 Việt Nam, dưới trướng HLV Miura, nhưng về lại đội bóng cũ, về lại môi trường V-League ', '<p><span style="color:rgb(0, 0, 0); font-family:times new roman; font-size:16px">Người ta cứ hay n&oacute;i tại sao C&ocirc;ng Phượng cứ phải chơi l&ugrave;i thấp khi đ&aacute; trong m&agrave;u &aacute;o đội tuyển U23 Việt Nam? Rồi tại sao Phượng phải bớt c&aacute; nh&acirc;n đi, phải chơi v&igrave; tập thể nhiều hơn l&uacute;c c&oacute; HLV Miura ngồi trong khu kỹ thuật?</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 0); font-family:times new roman; font-size:16px">Người ta cũng hay so s&aacute;nh C&ocirc;ng Phượng l&uacute;c đ&aacute; trong m&agrave;u &aacute;o đội tuyển U23 Việt Nam với C&ocirc;ng Phượng trong m&ocirc;i trường quen thuộc ở c&aacute;c giải trẻ v&agrave; ở V-League. Giờ th&igrave; người ta bắt đầu thấy kết quả, cầu thủ nổi tiếng nhất Việt Nam thời điểm hiện tại hiệu quả hơn hẳn khi nhận được những lần hướng dẫn của vị HLV người Nhật, so với sự lạc lỏng khi kh&ocirc;ng c&oacute; &ocirc;ng n&agrave;y theo d&otilde;i Phượng từ khu kỹ thuật.</span><br />\r\n<br />\r\n<span style="color:rgb(0, 0, 0); font-family:times new roman; font-size:16px">C&ocirc;ng Phượng trong m&agrave;u &aacute;o đội tuyển U23 Việt Nam v&agrave; C&ocirc;ng Phượng l&uacute;c đ&aacute; cho HA Gia Lai từ đầu V-League tới giờ gần như l&agrave; 2 cầu thủ ho&agrave;n to&agrave;n kh&aacute;c nhau, một người li&ecirc;n tục tỏa s&aacute;ng từ v&ograve;ng loại U23 ch&acirc;u &Aacute;, đến SEA Games 2015, c&ograve;n người kia gần như mất h&uacute;t ở đấu trường V-League.</span></p>\r\n', 31, 'vne-1435650769.jpg'),
(76, 'Ronaldinho tái ngộ Eto''o tại Thổ Nhĩ Kỳ', 'Mùa giải tới, Ronaldinho sẽ sát cánh cùng Samuel Eto''o trong màu áo Antalyaspor, đội bóng vừa trở lại giải đấu cao nhất của bóng đá Thổ Nhĩ Kỳ.', '<p><span style="color:rgb(0, 0, 0); font-family:times new roman; font-size:16px">Ronaldinho vừa đạt được thỏa thuận đầu qu&acirc;n cho Antalyaspor, đội b&oacute;ng gi&agrave;u tham vọng vừa trở lại giải đấu cao nhất của b&oacute;ng đ&aacute; Thổ Nhĩ Kỳ sau 13 năm chờ đợi. Đ&aacute;ng ch&uacute; &yacute;, tại đ&acirc;y &ldquo;Ro vẩu&rdquo; sẽ t&aacute;i ngộ người đồng đội từng thi đấu rất ăn &yacute; với anh trong m&agrave;u &aacute;o Barcelona, tiền đạo Samuel Eto&rsquo;o.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y h&ocirc;m qua, đ&iacute;ch th&acirc;n Chủ tịch Antalyaspor, &ocirc;ng Gultekin Gencer x&aacute;c nhận trước b&aacute;o giới về việc chi&ecirc;u mộ huyền thoại 35 tuổi người Brazil: &ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; đạt được một thỏa thuận với Ronaldinho. T&ocirc;i nghĩ rằng trong v&ograve;ng hai ng&agrave;y tới sẽ c&oacute; c&ocirc;ng bố ch&iacute;nh thức.&rdquo;</p>\r\n\r\n<p>Ngo&agrave;i ra, &ocirc;ng n&agrave;y c&ograve;n b&oacute;ng gi&oacute; về việc sẽ k&yacute; hợp đồng với một t&ecirc;n tuổi lớn nữa của b&oacute;ng đ&aacute; thế giới v&agrave; tếu t&aacute;o đ&ugrave;a rằng: &ldquo;B&ecirc;n cạnh Eto&#39;o v&agrave; Ronaldinho, ch&uacute;ng t&ocirc;i sẽ k&yacute; hợp đồng với một t&ecirc;n tuổi lớn nữa. Đến l&uacute;c ấy, ch&uacute;ng t&ocirc;i sẽ đốt ch&aacute;y cả thế giới!&rdquo;</p>\r\n\r\n<p>Ronaldinho cũng x&aacute;c nhận vụ việc đầu qu&acirc;n cho Antalyaspor khi th&ocirc;ng b&aacute;o cho c&aacute;c CĐV th&ocirc;ng qua một video đăng tr&ecirc;n Instagram: &ldquo;Ch&agrave;o tất cả mọi người. T&ocirc;i sẽ sớm c&oacute; mặt tại Thổ Nhĩ Kỳ c&ugrave;ng c&aacute;c bạn.&rdquo; Trước đ&oacute;, Ronaldinho đ&atilde; chấm dứt hợp đồng với đội b&oacute;ng Queretaro của Mexico.</p>\r\n\r\n<p>Đối với những người h&acirc;m mộ b&oacute;ng đ&aacute;, chắc hẳn bộ đ&ocirc;i Ronaldinho-Eto&rsquo;o l&agrave; những c&aacute;i t&ecirc;n đ&atilde; in s&acirc;u v&agrave;o t&acirc;m tr&iacute;. Hai si&ecirc;u sao n&agrave;y c&ugrave;ng với Giuly đ&atilde; tạo n&ecirc;n bộ ba tấn c&ocirc;ng c&oacute; sức c&ocirc;ng ph&aacute; khủng khiếp, gi&uacute;p Barca chinh phục danh hiệu Champions League 2005-06.</p>\r\n\r\n<p>Tuy đ&atilde; bước qua đỉnh cao của sự nghiệp nhưng chắc chắn việc chứng kiến Ronaldinho v&agrave; Eto&rsquo;o s&aacute;t c&aacute;nh sẽ đem lại ho&agrave;i cảm cho những người y&ecirc;u b&oacute;ng đ&aacute; tấn c&ocirc;ng v&agrave; Barca.</p>\r\n', 31, 'vne-1435650894.jpg'),
(77, 'Sergio Ramos đòi chuyển tới MU', 'Theo tờ Sky Sports, trung vệ Sergio Ramos đã yêu cầu BLĐ Real Madrid cho phép chuyển tới thi đấu cho MU. Đây là lợi thế của "Quỷ đỏ" trong thương vụ này.', '<p><span style="color:rgb(0, 0, 0); font-family:times new roman; font-size:16px">Theo nguồn tin từ tờ&nbsp;</span><em>Sky Sports</em><span style="color:rgb(0, 0, 0); font-family:times new roman; font-size:16px">, trung vệ Sergio Ramos đ&atilde; y&ecirc;u cầu BLĐ Real Madrid cho ph&eacute;p chuyển sang kho&aacute;c &aacute;o MU. Đ&acirc;y l&agrave; lợi thế kh&ocirc;ng nhỏ của đội b&oacute;ng th&agrave;nh Manchester trong thương vụ n&agrave;y.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cũng theo tờ b&aacute;o n&agrave;y, MU đ&atilde; gửi lời đề nghị trị gi&aacute; 28,6 triệu bảng (40 triệu euro) tới Real Madrid. Tuy nhi&ecirc;n, l&uacute;c n&agrave;y,<em>&nbsp;Los Blancos&nbsp;</em>đang đ&ograve;i hỏi mức gi&aacute; 65 triệu bảng mới nhả Sergio Ramos.</p>\r\n\r\n<p>Theo chuy&ecirc;n gia của đ&agrave;i Graham Hunter, khả năng &quot;Quỷ đỏ&quot; th&agrave;nh c&ocirc;ng trong thương vụ n&agrave;y l&agrave; kh&ocirc;ng nhỏ. &Ocirc;ng n&oacute;i: &ldquo;Sergio Ramos đ&atilde; n&oacute;i với Real Madrid rằng anh ấy đ&atilde; sẵn s&agrave;ng rời khỏi CLB trong m&ugrave;a H&egrave; n&agrave;y.</p>\r\n\r\n<p>C&oacute; một điểm đ&aacute;ng lưu &yacute; rằng cậu ấy y&ecirc;u cầu chỉ đ&agrave;m ph&aacute;n với MU. Nhiều người nghi ngờ rằng Sergio Ramos đang lợi dụng &quot;Quỷ đỏ&quot; giống như Cesc Fabregas trước đ&acirc;y nhưng t&ocirc;i thấy khả năng th&agrave;nh c&ocirc;ng của MU rất cao. Họ đang thể hiện sự quyết t&acirc;m lớn trong vụ n&agrave;y&rdquo;.</p>\r\n\r\n<p>Trong m&ugrave;a H&egrave; năm ngo&aacute;i, Real Madrid cũng đ&atilde; để Di Maria tới MU với mức ph&iacute; kỷ lục 59,7 triệu bảng. Do đ&oacute;, c&oacute; thể đội b&oacute;ng Ho&agrave;ng gia T&acirc;y Ban Nha cũng kh&ocirc;ng để Sergio Ramos ra đi với mức ph&iacute; &quot;b&egrave;o&quot;.</p>\r\n', 31, 'vne-1435650958.jpg');

